mapper.insert:
insert into review values (
    (select nvl(max(rnum)+1,1) from review),
    #{writer},sysdate+1,#{rmark},#{rcomment},
    (select nvl(max(rnum)+1,1) from review),
    #{movie_num}
)

mapper.select:

select * from (
    select rownum rn, A.* from (
        select * from review where movie_num = #{movie_num} order by rnum
    )A
)where rn between #{start} and #{end}
